import { Subject } from 'rxjs';
export class SubscriberService {
  constructor() {
    this.orderChanged = new Subject();
    this.order = [];
  }

  setOrder(order) {
    this.order = order;
    this.orderChanged.next(this.order);
  }

  getOrders() {
    return this.order.slice();
  }

  addOrder(order) {
    this.order.push(order);
    this.orderChanged.next(this.order.slice());
  }

  getPlanType(email) {
    for (var i = 0; i < this.order.length; i++) {
      if (this.order[i].email === email) {
        return this.order[i].planType;
      }
    }

    return "noPlan";
  }

  updateOrder(index, newOrder) {
    this.order[index] = newOrder;
    this.orderChanged.next(this.order.slice());
  }

}
